&lt;figure class=&#34;highlight shell&#34;&gt;&lt;figcaption data-lang=&#34;Bash&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2022&lt;/span&gt;/08/24 &lt;span class=&#34;token number&#34;&gt;19&lt;/span&gt;:12:24 Adding service: LogService with URL: http://localhost:4000&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;2&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2022&lt;/span&gt;/08/24 &lt;span class=&#34;token number&#34;&gt;19&lt;/span&gt;:12:24 Heartbeat check passed &lt;span class=&#34;token keyword&#34;&gt;for&lt;/span&gt; LogService&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;3&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2022&lt;/span&gt;/08/24 &lt;span class=&#34;token number&#34;&gt;19&lt;/span&gt;:12:26 Request received&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;4&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2022&lt;/span&gt;/08/24 &lt;span class=&#34;token number&#34;&gt;19&lt;/span&gt;:12:26 Adding service: GradingService with URL: http://localhost:6000&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;5&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2022&lt;/span&gt;/08/24 &lt;span class=&#34;token number&#34;&gt;19&lt;/span&gt;:12:27 Heartbeat check passed &lt;span class=&#34;token keyword&#34;&gt;for&lt;/span&gt; LogService&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;6&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2022&lt;/span&gt;/08/24 &lt;span class=&#34;token number&#34;&gt;19&lt;/span&gt;:12:30 Heartbeat check passed &lt;span class=&#34;token keyword&#34;&gt;for&lt;/span&gt; GradingService&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;7&#34;&gt;&lt;/td&gt;&